import React, { useEffect, useState } from "react";
import "./Coin.css";
import { Button } from "web3uikit";
import {useWeb3ExecuteFunction, useMoralis} from "react-moralis";


function Coin({perc, setPerc, token, setModalToken, setVisible}) {

const [color, setColor] = useState();
const contractProcessor = useWeb3ExecuteFunction();
const {isAuthenticated} = useMoralis();

useEffect(() => {
if (perc < 50) {
  setColor("#c43d08");
} else {
  setColor("green");
}
}, [perc]);

async function vote(upDown) {
  let options = {
    contractAddress: "",
    functionName: "",
    abi: [],
  }
}

  return (
    <>
      <div>
        <div className="token">
            {token}
        </div>
        <div className="circle" style={{ boxShadow: `0 0 70px ${color}`}}>
          <div className="wave"
          style={{marginTop: `${100 - perc}%`,
          boxShadow: `0 0 70px ${color}`,
          backgroundColor: color,
            }}
          >

          </div>
          <div className="percentage">
            {perc}%
          </div>
          </div>
          <div className="votes">
               <Button 
               onClick={() => {setPerc(perc + 1)}}
               text="Green"
               theme="primary"
               type="button"
               />

              <Button 
               onClick={() => {setPerc(perc - 1)}}
               text="Red"
               theme="colored"
               type="button"
               />
          </div>
          <div className="votes">
           <Button onClick={() => {
             setModalToken(token)
             setVisible(true);
           }}
           text="INFO"
           theme="translucent"
           type="button"
           />
          </div>
      </div>
    </>
  );
}

export default Coin;
