import React, { useEffect, useState } from "react";
import "./App.css";
import logo from "./images/plsalogo.jpg";
import { ConnectButton, Modal } from "web3uikit";
import Coin from "./components/Coin"
import { abouts } from "./about";
import { useMoralisWeb3Api, useMoralis } from "react-moralis";



const App = () => {

  const [btc, setBtc] = useState(90);
  const [pls, setPls] = useState(70);
  const [plsx, setPlsx] = useState(44);
  const [hex, setHex] = useState(80);
  const [loan, setLoan] = useState(51);
  const [phiat, setPhiat] = useState(38);
  const [Moralis, isInitialized] = useMoralis();

  const [modalPrice, setModalPrice] = useState();
  const Web3Api = useMoralisWeb3Api();

  const [visible, setVisible] = useState(false);
  const [modalToken, setModalToken] = useState();

  async function getRatio(tick, setPerc) {
    const Votes = Moralis.Object.extend("Voters");
    const query = new Moralis.Query(Votes);
    query.equalTo("ticker", tick);
    query.descending("createdAt");
    const results = await query.first();
    let up = Number(results.attributes.up);
    let down = Number(results.attributes.down);
    let ratio = Math.round(up/(up+down)*100);
    setPerc(ratio);
  }

  useEffect(() =>{
     if(isInitialized){
       getRatio("BTC", setBtc);
       getRatio("PLS", setPls);
       getRatio("PLSX", setPlsx);
       getRatio("PHIAT", setPhiat);
       getRatio("LOAN", setLoan);
     }
  }, [isInitialized]);

  useEffect(() => {

    async function fetchTokenPrice() {
      const options = {
        address:
        abouts[abouts.findIndex((x) => x.token === modalToken)].address,
      };
      const price = await Web3Api.token.getTokenPrice(options);
      setModalPrice(price.usdPrice.toFixed(2));
    }
    if(modalToken) {
      fetchTokenPrice()
    }
  }, [modalToken]);

  return (
    <>
     <div className="header">
       <div className="logo">
         <img src={logo} alt="PulseAppsLogo" height="50px"/>
         PulseApps
       </div>
       <ConnectButton />
     </div>
     <div className="instructions">
       Should these Projects get listed on PulseAppStore? Green or Red Flag ?
     </div>
     <div className="list">
     <Coin 
     perc={btc}
     setPerc={setBtc}
     token={"BTC"}
     setModalToken={setModalToken}
     setVisible={setVisible}
     />
     <Coin 
     perc={pls}
     setPerc={setPls}
     token={"PLS"}
     setModalToken={setModalToken}
     setVisible={setVisible}
     />
     <Coin 
     perc={plsx}
     setPerc={setPlsx}
     token={"PLSX"}
     setModalToken={setModalToken}
     setVisible={setVisible}
     />
     <Coin 
     perc={hex}
     setPerc={setHex}
     token={"HEX"}
     setModalToken={setModalToken}
     setVisible={setVisible}
     />
     <Coin 
     perc={loan}
     setPerc={setLoan}
     token={"LOAN"}
     setModalToken={setModalToken}
     setVisible={setVisible}
     />
     <Coin 
     perc={phiat}
     setPerc={setPhiat}
     token={"PHIAT"}
     setModalToken={setModalToken}
     setVisible={setVisible}
     />
     </div>

     <Modal 
     isVisible={visible}
     onCloseButtonPressed={() => setVisible(false)}
     hasFooter={false}
     title={modalToken}
     >
       <div>
       <span style={{color: "blue"}}>{`Price: `}</span>
       {modalPrice}$
       </div>
       <div>
         <span style={{color: "blue"}}>{`About`}</span>
       </div>
       <div>
         {modalToken &&
         abouts[abouts.findIndex((x) => x.token === modalToken)].about}
       </div>
     </Modal>
    </>
  );
};

export default App;
